    // my solution but causing TLE in bigger datasets
    public static int[][] nearest0(int[][] mat) {
        int row = mat.length;
        int col = mat[0].length;
        int[][] ans = new int[row][col];
        for (int i = 0; i < row; i++) {
            for (int j = 0; j < col; j++) {
                if (mat[i][j] == 0) {
                    ans[i][j] = 0;
                } else {
                    ans[i][j] = distanceByBfs(i, j, mat);
                }
            }
        }
        return ans;
    }

    private static int distanceByBfs(int i, int j, int[][] mat) {
        int n = mat.length;
        int m = mat[0].length;
        int[][] vis = new int[n][m];
        int[] delRow = new int[]{-1,0,+1,0};
        int[] delCol = new int[]{0,+1,0,-1};
        Queue<PairC> queue = new LinkedList<>();
        queue.offer(new PairC(i,j,0));
        vis[i][j] = 1;
        while (!queue.isEmpty()){
            PairC pairC = queue.poll();
            int row = pairC.first;
            int col = pairC.second;
            int distance = pairC.time;
            for(int k=0 ; k<4; k++){
                int nrow = row + delRow[k];
                int ncol = col + delCol[k];
                if(nrow>=0 && nrow <n && ncol>=0 && ncol<m){
                    if(mat[nrow][ncol] == 0 && vis[nrow][ncol] != 1){
                        return distance +1;
                    }
                    else if(vis[nrow][ncol] != 1) {
                        queue.offer(new PairC(nrow, ncol, distance +1));
                        vis[nrow][ncol] = 1;
                    }
                }
            }
        }
        return -1;
    }


    public static int[][] nearest0Optimized(int[][] mat){
        int[] delRow = new int[]{-1,0,+1,0};
        int[] delCol = new int[]{0,+1,0,-1};
        int row = mat.length;
        int col = mat[0].length;
        int[][] ans = new int[row][col];
        int[][] vis = new int[row][col];
        Queue<PairC> queue = new LinkedList<>();
        for (int i=0; i<row; i++){
            for(int j=0; j<col; j++){
                if(mat[i][j] == 0){
                    queue.offer(new PairC(i,j,0));
                    vis[i][j] = 1;
                }
            }
        }
        while(!queue.isEmpty()){
            PairC pairC= queue.poll();
            int r = pairC.first;
            int c = pairC.second;
            int distance = pairC.time;
            ans[r][c] = distance;
            for(int k=0 ; k<4; k++){
                int nrow = r + delRow[k];
                int ncol = c + delCol[k];
                if(nrow>=0 && nrow <row && ncol>=0 && ncol<col && vis[nrow][ncol]!=1){
                    queue.offer(new PairC(nrow,ncol,distance+1));
                    vis[nrow][ncol] = 1;
                }
            }
        }
        return ans;
    }
